presentation.customCSS:
@import url("https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.2/css/all.min.css");
@import url("https://fonts.googleapis.com/css2?family=Inter:wght@400;500;700&display=swap");

.icon-box {
  opacity: 1;
  filter: none;
  transition: opacity 0.3s;
}

.icon-box.icon-disabled {
  opacity: 0.1; /* Icône grisée */
  filter: grayscale(100%);
}

.title {
  font-size: 18px;
  color: #b88c63;
  display: block;   /* ← pour que le titre soit sur sa propre ligne */
}

.titlebold {
  font-size: 18px;
  color: #b88c63;
  font-weight: bold;
  display: block;   /* ← pour que le titre soit sur sa propre ligne */
}


.fa-solid,
.fa-regular,
.fa-brands,
.icon-box i {
  font-style: normal !important;
}

  /* Liste des recos */
  .tasks {
    list-style: none;
    margin: 0;
    padding: 0;
    display: grid;
    gap: 10px;
  }

  .task {
    display: flex;
    gap: 10px;
    align-items: flex-start;
  }

  .icon-box {
    width: 24px;
    height: 24px;
    min-width: 24px;
    border-radius: 6px;
    display: inline-flex;
    align-items: center;
    justify-content: center;
  }
  .icon-box .fa-solid { font-size: 14px; line-height: 1; }

  /* Variantes couleur/picto (adaptées à tes sections) */
  .icon-default    { background:#e3f1ec; }
  .icon-default .fa-solid { color:#2d8860; }

  .icon-supplement  { background:#eae5f7; }
  .icon-supplement .fa-solid { color:#584092; }

  .icon-nutrition  { background:#eff3d9; }
  .icon-nutrition .fa-solid { color:#b0c054; }

  .icon-lifestyle  { background:#fdece1; }
  .icon-lifestyle .fa-solid { color:#f48744; }

  .icon-medicament { background:#eae5f7; }
  .icon-medicament .fa-solid { color:#7f5ecf; }

  .task-content { display:flex; flex-direction:column; gap:2px; }
  .task-title   { font-biomarker_weight:500; font-size:15px; color:#000; }
  .task-sub     { color:#6b7280; font-size:12px; }

