function prevSlideFromSynthese() {

  if (window.mfrom === "#synthese") return "#parcours_examens2";

  // "#synthese"
  return getOb("n", 2, "label")==""?"#obj_futur_2":"#obj_futur_3";
}

function nextSlideFromSynthese() {

   if (window.mfrom === "#synthese1") return "#obj_futur_1_recos";

// "#synthese1"
  return hasObjN_1()?"#obj_pass_1":"#objavenir";
}

function getT(val, exists=true, hide=false) {

  let src = localStorage.getItem('previousSlide'); 

  if (hide) {
    val = "<span style=\"color:#FFFFFF\">" + val + "</span>"; 
    return val;
  }   

  if (!exists) {
    val = "<span style=\"color:#DDDDDD\">" + val + "</span>"; 
  }


  if (titreExiste(val) && window.mfrom != "#synthese") {
    val = "<span style=\"color:#CCC\">" + val + "</span>"; 
  }

return val;
}

function getPreviousSlide() {
  return localStorage.getItem('previousSlide') || '';
}

function bulletTestN(item, show = false) {

  if (getPI("gender") === "female") {
    if (item === "echovasc") item = item + "_femme";
    if (item === "echorein") item = item + "_femme";
    if (item === "echodig1") item = item + "_femme";
    if (item === "echodig2") item = item + "_femme";
    if (item === "echoendo") item = item + "_femme";
    if (item === "bioendo2") item = item + "_femme";
  }

  const marker   = getTestMarkerForSlide(item);
  const val      = getCU(marker, "n", "value");
  const inter    = getCU(marker, "n", "interpretation");
  const interCom = getCU(marker, "n", "interpretationComment");

  const res = (val != null) || (inter != null) || (interCom != null);

  if (show) {
    return `res:+${res}+ 
item:+${item}+  
marker:+${marker}+  
val:+${val}+  
inter:+${inter}+  
intercom:+${interCom}+`;
  }

  return res;
}

function bulletTestN_1(item, show = false) {

  if (getPI("gender") === "female") {
    if (item === "echovasc") item = item + "_femme";
    if (item === "echorein") item = item + "_femme";
    if (item === "echodig1") item = item + "_femme";
    if (item === "echodig2") item = item + "_femme";
    if (item === "echoendo") item = item + "_femme";
    if (item === "bioendo2") item = item + "_femme";
  }

  const marker   = getTestMarkerForSlide(item);
  if (!marker) {
    if (show) {
      return `res:+false+ 
item:+${item}+  
marker:+${marker}+ (❌ introuvable)`;
    }
    return false;
  }

  const val       = getCU(marker, "n",   "value");
  const inter     = getCU(marker, "n",   "interpretation");
  const interCom  = getCU(marker, "n",   "interpretationComment");

  const val1      = getCU(marker, "n-1", "value");
  const inter1    = getCU(marker, "n-1", "interpretation");
  const interCom1 = getCU(marker, "n-1", "interpretationComment");

  // On veut quelque chose aux deux cycles + pas CU1
  const res =
    ((val  != null) || (inter  != null) || (interCom  != null)) &&
    ((val1 != null) || (inter1 != null) || (interCom1 != null)) &&
    isNotCU1();

  if (show) {
    return `res:+${res}+ 
item:+${item}+  
marker:+${marker}+  
val:+${val}+  
inter:+${inter}+  
intercom:+${interCom}+  
val1:+${val1}+  
inter1:+${inter1}+  
intercom1:+${interCom1}+
isnotCU1:+${isNotCU1()}`;
  }

  return res;
}

function getTestMarkerForSlide(slideCode) {
  const groupName = getGroupName();
  const mmap = navSyntheseMap2;
  const arr = mmap.get(groupName);

console.warn("🔍 Arr from groupName =", groupName, arr);


  if (!arr) {
    console.warn(`❌ Groupe ${groupName} introuvable dans navSyntheseMap2.`);
    return null;
  }

  for (const item of arr) {
    if (item?.slideCode === slideCode) {

//     console.warn("*****+" +  item.testMarker + "+" );
      return item.testMarker ?? null;
    }
  }

  console.warn(`⚠️ Aucun testMarker trouvé pour slideCode "${slideCode}" dans "${groupName}".`);
  return null;
}

function getSlideCode2(id, direction, store = false, currentSlideCode = null) {


  let src = localStorage.getItem('previousSlide'); 
  let val = "";
  console.warn(" slide code : *******"+src+"*");


  if ((src == "#synthese") || (src == "#synthese1")) {

    if (direction == 1) val = getPrevNext2(getGroupName(), currentSlideCode).next;
    if (direction == -1) val = getPrevNext2(getGroupName(), currentSlideCode).previous;
    if (val == "#----------") return src;

    return val;
  }

  id = id + direction; 
  if (store) setLS("idMarker", id);

  val = getObjInfo(
      "code",
      localStorage.getItem("numObjT"),
      id,
      localStorage.getItem("numCU"));

  if (val =='') {
    val = getPreviousSlide();
  }

  return val;
}

function getPrevNext2(groupName, currentSlideCode) {
  const mmap = navSyntheseMap2; // nouvelle map
  const arr = mmap.get(groupName); // accès Map identique

  if (!arr) {
    console.error(`❌ Groupe ${groupName} introuvable. Clés :`, [...mmap.keys()]);
    return { previous: "#synthese1", next: "#synthese1" };
  }

  // on ne garde que les éléments qui ont un slideCode
  const slides = arr.filter(item => item && item.slideCode);

  const idx = slides.findIndex(item => item.slideCode === currentSlideCode);
  if (idx === -1) {
    console.error(`❌ slideCode "${currentSlideCode}" introuvable dans "${groupName}".`);
    return { previous: "#synthese1", next: "#synthese1" };
  }

  let previous = idx > 0 ? "#" + slides[idx - 1].slideCode : "#synthese1";
  let next     = idx < slides.length - 1 ? "#" + slides[idx + 1].slideCode : "#synthese1";

  return { previous, next };
}

function ifNull(val, ifnullstring) {

  if (val ==  null) return ifnullstring;
  return val;
}

