function listRecommendationIconsBySlide(slideCode) {
  const cycle = "n";
  const group = "reco_" + cuType(); // même logique que la fonction complète

  const typeOrder = ["exam", "therapeutic", "supplement", "nutrition", "lifestyle", "coaching", "other"];

  if (!window.checkupMap || typeof checkupMap.get !== "function") {
    return "❌ checkupMap introuvable ou invalide";
  }

  const checkups = checkupMap.get("checkups");
  const recosMap = checkups?.[cycle]?.recommendations;
  if (!recosMap) return "";

  const wanted = findMarkersBySlide(slideCode, group, false);
  if (!Array.isArray(wanted) || wanted.length === 0) return "";
  const wantedSet = new Set(wanted.map(String));

  const allRecos = (recosMap instanceof Map)
    ? Array.from(recosMap.values())
    : Object.values(recosMap || {});

  const recos = allRecos.filter(r => {
    const obs = Array.isArray(r?.observationCodes) ? r.observationCodes.map(String) : [];
    return obs.some(code => wantedSet.has(code));
  });

  // --- Helpers ---
  const normalizeType = (t) => {
    const k = String(t ?? "other").trim().toLowerCase();
    if (["exam","examen","examens","complémentaire","complementaire"].includes(k)) return "exam";
    if (["supplement","supplément","complement"].includes(k)) return "supplement";
    if (["lifestyle","mode de vie","mode_de_vie"].includes(k)) return "lifestyle";
    if (["therapeutic","médicament","medicament","treatment"].includes(k)) return "therapeutic";
    if (["nutrition","diet"].includes(k)) return "nutrition";
    if (["coaching","coach","accompagnement"].includes(k)) return "coaching";
    return "other";
  };

  const sectionLabel = {
    exam:        "Examens complémentaires",
    supplement:  "Compléments alimentaires",
    lifestyle:   "Mode de vie",
    therapeutic: "Médicaments",
    nutrition:   "Nutrition",
    coaching:    "Coaching",
    other:       "Autres"
  };

  const iconForType = (typeKey) => {
    switch (typeKey) {
      case "supplement":  return '<i class="fa-solid fa-capsules"></i>';
      case "nutrition":   return '<i class="fa-solid fa-apple-whole"></i>';
      case "lifestyle":   return '<i class="fa-solid fa-icons"></i>';
      case "therapeutic": return '<i class="fa-solid fa-prescription-bottle-medical"></i>';
      case "exam":        return '<i class="fa-solid fa-clipboard-check"></i>';
      default:            return '<i class="fa-solid fa-circle-check"></i>';
    }
  };

  // 🔍 Types présents sur cette slide
  const typeSet = new Set();
  for (const r of recos) typeSet.add(normalizeType(r?.type));

  // Affiche TOUTES les catégories, en mettant en dernier celles hors ordre
  const allTypes = [
    ...typeOrder,
    ...Array.from(typeSet).filter(k => !typeOrder.includes(k)).sort()
  ];

  let html = '<div class="reco-type-icons" style="display:flex;gap:6px;align-items:center;">';

  for (const key of allTypes) {
    const active = typeSet.has(key);
    const iconHtml = iconForType(key);
    const label = sectionLabel[key] ?? key;

    html += `
      <span class="icon-box ${active ? "" : "icon-disabled"}"
            title="${label}"
            style="display:inline-flex;align-items:center;justify-content:center;">
        ${iconHtml}
      </span>`;
  }

  html += "</div>";
  return html;
}

function titreExiste(titreCherche, cycle = "n") {
  if (!titreCherche) return false;

  // Sélection du bon tableau selon le cycle
  const titresRoot = (cycle === "n")
    ? window.allTitres
    : window.allTitresN_1;

  if (!titresRoot || typeof titresRoot !== "object") {
    console.warn("⚠️ titresRoot invalide pour cycle", cycle);
    return false;
  }

  // Parcours de chaque liste de titres
  for (const key in titresRoot) {
    const titresArray = titresRoot[key];
    if (!Array.isArray(titresArray)) continue;

    if (titresArray.includes(titreCherche)) {
      return true;
    }
  }

  return false;
}

function getSlideCode(id, direction, store = false, currentSlideCode = null) {


  let src = localStorage.getItem('previousSlide'); 
  let val = "";
  console.warn(" slide code : *******"+src+"*");


  if ((src == "#synthese") || (src == "#synthese1")) {

    if (direction == 1) val = getPrevNext(getGroupName(), currentSlideCode).next;
    if (direction == -1) val = getPrevNext(getGroupName(), currentSlideCode).previous;
    if (val == "#----------") return src;

    return val;
  }

  id = id + direction; 
  if (store) setLS("idMarker", id);

  val = getObjInfo(
      "code",
      localStorage.getItem("numObjT"),
      id,
      localStorage.getItem("numCU"));

  if (val =='') {
    val = getPreviousSlide();
  }

  return val;
}

