function listNavMap() {

  if (!window.navSyntheseMap || typeof navSyntheseMap.get !== "function") {
    console.error("❌ navSyntheseMap introuvable ou invalide");
    return;
  }

  console.warn("📌 Liste complète de navSyntheseMap :");

  for (const [groupName, arr] of navSyntheseMap.entries()) {
    console.log(`🔹 Groupe : "${groupName}"`);

    if (!Array.isArray(arr)) {
      console.warn(`⚠️ Valeur non-array pour "${groupName}" :`, arr);
      continue;
    }

