function updateValueInGroup(oldValue, newValue) {

  const groupName = getGroupName();
  const mmap = navSyntheseMap; 
  const arr = mmap.get(groupName);

  if (!arr) {
    console.warn(`⚠️ Groupe "${groupName}" introuvable`);
    return false;
  }

  const index = arr.indexOf(oldValue);
  if (index === -1) {
    console.warn(`⚠️ Valeur "${oldValue}" introuvable dans "${groupName}"`);
    return false;
  }

  arr[index] = newValue;
  mmap.set(groupName, arr); // Mise à jour Map

  console.warn(`✏️ "${oldValue}" → "${newValue}" dans "${groupName}"`);
  return true;
}

function removeValueNavMap(valueToRemove) {

  const groupName = getGroupName();
  const mmap = navSyntheseMap; 
  const arr = mmap.get(groupName);

  if (!arr) {
    console.warn(`⚠️ Groupe "${groupName}" introuvable`);
    return false;
  }

  const index = arr.indexOf(valueToRemove);
  if (index === -1) {
    console.warn(`⚠️ Valeur "${valueToRemove}" introuvable dans "${groupName}"`);
    return false;
  }

  arr.splice(index, 1); 
  mmap.set(groupName, arr); // mise à jour Map

  console.log(`🗑️ Suppression OK → "${valueToRemove}" retiré de "${groupName}"`);
  return true;
}

