function getPrevNext(groupName, item) {
  const mmap = navSyntheseMap;               // même style que ton code
  const arr = mmap.get(groupName);           // accès Map

  if (!arr) {
    console.error(`❌ Groupe ${groupName} introuvable. Clés dispo :`, [...mmap.keys()]);
    return { previous: null, next: null };
  }

  const idx = arr.indexOf(item);
  if (idx === -1) {
    console.error(`❌ Item "${item}" introuvable dans le groupe "${groupName}".`);
    return { previous: null, next: null };
  }

  let previous = idx > 0 ? "#" + arr[idx - 1] : null;
  let next     = idx < arr.length - 1 ? "#" + arr[idx + 1] : null;

 if (previous == null) previous = "#synthese1";
 if (next     == null) next = "#synthese1";

  return { previous, next };
}

function listRecommendationsWithoutObjectivesGroupedByType(
  cycle = "n",
  {
        typeOrder = ["exam", "therapeutic", "supplement", "nutrition", "lifestyle", "coaching", "other"],
  } = {}
) {
  if (!window.checkupMap || typeof checkupMap.get !== "function") {
    return "❌ checkupMap introuvable ou invalide";
  }

  const recosMap = checkupMap.get("checkups")?.[cycle]?.recommendations;
  if (!recosMap) return `❌ Pas de recommandations trouvées pour le cycle "${cycle}"`;

  // Normaliser en tableau
  const allRecos = (recosMap instanceof Map)
    ? Array.from(recosMap.values())
    : Object.values(recosMap || {});

  // Filtrer : objectiveIds vide / absent
  const filtered = allRecos.filter(r =>
    !r?.objectiveIds || (Array.isArray(r.objectiveIds) && r.objectiveIds.length === 0)
  );

  console.warn("📋 Recos sans objectiveIds =", filtered.length);

  if (filtered.length === 0) {
    return `<span style="font-size:20px; color:#b88c63; font-weight:700;">Recommandations sans Objectif</span><br><br>✅ Aucune recommandation.`;
  }

  // --- Normalisation type + labels par section (mêmes règles que tes autres fonctions) ---
  const normalizeType = (t) => {
    const k = String(t ?? "other").trim().toLowerCase();
    if (["exam","examen","examens","complémentaire","complementaire","exams"].includes(k)) return "exam";
    if (["supplement","supplément","supplements","suppléments","complement","complément"].includes(k)) return "supplement";
    if (["lifestyle","mode de vie","mode_de_vie","lifestylehabit"].includes(k)) return "lifestyle";
    if (["therapeutic","médicament","medicament","drug","treatment","traitement"].includes(k)) return "therapeutic";
    if (["nutrition","diet"].includes(k)) return "nutrition";
    if (["coaching","coach","accompagnement"].includes(k)) return "coaching";
    return "other";
  };

  const sectionLabel = {
    exam:        "Examens complémentaires",
    supplement:  "Compléments alimentaires",
    lifestyle:   "Mode de vie",
    therapeutic: "Médicaments",
    nutrition:   "Nutrition",
    coaching:    "Coaching",
    other:       "Autres"
  };

  const iconForType = (typeKey) => {
    switch (typeKey) {
      case "supplement":
        return { boxClass: "icon-supplement",   iconHtml: '<i class="fa-solid fa-capsules"></i>' };
      case "nutrition":
        return { boxClass: "icon-nutrition",    iconHtml: '<i class="fa-solid fa-apple-whole"></i>' };
      case "lifestyle":
        return { boxClass: "icon-lifestyle",    iconHtml: '<i class="fa-solid fa-icons"></i>' };
      case "therapeutic":
        return { boxClass: "icon-medicament",   iconHtml: '<i class="fa-solid fa-prescription-bottle-medical"></i>' };
      case "exam":
        return { boxClass: "icon-default",      iconHtml: '<i class="fa-solid fa-clipboard-check"></i>' };
      default:
        return { boxClass: "icon-default",      iconHtml: '<i class="fa-solid fa-circle-check"></i>' };
    }
  };

  // Tri alpha + regroupement par type
  filtered.sort((a,b)=> (a?.label ?? "").localeCompare(b?.label ?? "", "fr", {sensitivity:"base"}));
  const groups = new Map();
  for (const r of filtered) {
    const key = normalizeType(r?.type);
    if (!groups.has(key)) groups.set(key, []);
    groups.get(key).push(r);
  }

  const orderedKeys = [
    ...typeOrder.filter(k => groups.has(k)),
    ...Array.from(groups.keys()).filter(k => !typeOrder.includes(k)).sort()
  ];

  // --- HTML final (même format que listRecommendationsBySlideStyled) ---
  let html = `<span style="font-size:24px; color:#b88c63; font-biomarker_weight:700;">Recommandations</span><br><br>`;
  for (const key of orderedKeys) {
    const arr = groups.get(key);
    const title = sectionLabel[key] ?? key;

    html += `<BR><p style="color:#000;font-biomarker_weight:700;font-size:12px">${title}</p>`;
    html += `<ul class="tasks">`;

    for (const r of arr) {
      const { boxClass, iconHtml } = iconForType(key);
      const label = r?.label ?? "❓label manquant";
      const desc  = r?.description ? `<span class="task-sub">${r.description}</span>` : "";
      html += `
        <li class="task">
          <span class="icon-box ${boxClass}">
            ${iconHtml}
          </span>
          <div class="task-content">
            <span class="task-title">${label}</span>
            ${desc}
          </div>
        </li>`;
    }

    html += `</ul>`;
  }

  return html;
}

