function listRecommendationsForObjectiveByTypeStyled2(
  cycle = "n",
  objectiveIndex = 0,
  {
    typeOrder = ["exam", "therapeutic", "supplement", "nutrition", "lifestyle", "coaching", "other"],
  } = {}
) {
  // --- Vérifs de base ---
  if (!window.checkupMap || typeof checkupMap.get !== "function") {
    return "❌ checkupMap introuvable ou invalide";
  }
  const scope = checkupMap.get("checkups")?.[cycle];
  if (!scope) return `❌ Pas de données pour le cycle "${cycle}"`;

  const objectives = scope.objectives;
  const recosRaw   = scope.recommendations;

  if (!Array.isArray(objectives)) return `❌ Pas d'objectifs trouvés pour le cycle "${cycle}"`;
  if (objectiveIndex < 0 || objectiveIndex >= objectives.length) {
    return `❌ L'objectif ${objectiveIndex} n'existe pas (max = ${objectives.length - 1})`;
  }
  if (!recosRaw) return `❌ Pas de recommandations trouvées pour le cycle "${cycle}"`;

  const objective = objectives[objectiveIndex];

  // --- Codes de l’objectif ---
  const objectiveCodes = new Set([...(objective?.observationCodes || [])].map(String).filter(Boolean));
  if (objectiveCodes.size === 0) {
    console.warn("⚠️ Objectif sans observationCodes :", { objectiveIndex, objective });
  }

  // --- Normaliser recos en tableau ---
  const allRecos = (recosRaw instanceof Map)
    ? Array.from(recosRaw.values())
    : (Array.isArray(recosRaw) ? recosRaw : Object.values(recosRaw));

  // --- Collecte des cibles possibles d'une reco ---
  const getRecoTargets = (r) => {
    const items = (r?.items || [])
      .map(it => (typeof it === "string" ? it : (it?.code ?? it?.id)))
      .filter(Boolean);
    return new Set([
      ...(Array.isArray(r?.observationCodes) ? r.observationCodes : []),
      ...(Array.isArray(r?.targets) ? r.targets : []),
      ...(Array.isArray(r?.targetCodes) ? r.targetCodes : []),
      ...items
    ].map(String).filter(Boolean));
  };

  // --- Filtrer: intersection observationCodes ---
  const recosForObjective = [];
  let emptyTargets = 0, noMatch = 0;

  for (const r of allRecos) {
    const targets = getRecoTargets(r);
    if (targets.size === 0) { emptyTargets++; continue; }
    const hit = [...targets].some(code => objectiveCodes.has(code));
    console.warn("➡️ reco id=", r?.id, "| targets=", [...targets], "| hit=", hit);
    if (hit) recosForObjective.push(r); else noMatch++;
  }

  console.warn("📊 Stats filtre objectif =", {
    objectiveIndex,
    objectiveCodes: [...objectiveCodes],
    totalRecos: allRecos.length,
    matched: recosForObjective.length,
    emptyTargets,
    noMatch
  });

  if (recosForObjective.length === 0) {
    // Même entête que la fonction par slide, pour garder l’aspect
    return `<span style="font-size:24px; color:#b88c63; font-biomarker_weight:700;">Recommandations</span><br><br>(aucune recommandation liée trouvée)`;
  }

  // ----- UI identique à listRecommendationsBySlideStyled -----
  const normalizeType = (t) => {
    const k = String(t ?? "other").trim().toLowerCase();
    if (["exam","examen","examens","complémentaire","complementaire","exams"].includes(k)) return "exam";
    if (["supplement","supplément","supplements","suppléments","complement","complément"].includes(k)) return "supplement";
    if (["lifestyle","mode de vie","mode_de_vie","lifestylehabit"].includes(k)) return "lifestyle";
    if (["therapeutic","médicament","medicament","drug","treatment","traitement"].includes(k)) return "therapeutic";
    if (["nutrition","diet"].includes(k)) return "nutrition";
    if (["coaching","coach","accompagnement"].includes(k)) return "coaching";
    return "other";
  };

  const sectionLabel = {
    exam:        "Examens complémentaires",
    supplement:  "Compléments alimentaires",
    lifestyle:   "Mode de vie",
    therapeutic: "Médicaments",
    nutrition:   "Nutrition",
    coaching:    "Coaching",
    other:       "Autres"
  };

  const iconForType = (typeKey) => {
    switch (typeKey) {
      case "supplement":
        return { boxClass: "icon-supplement",   iconHtml: '<i class="fa-solid fa-capsules"></i>' };
      case "nutrition":
        return { boxClass: "icon-nutrition",    iconHtml: '<i class="fa-solid fa-apple-whole"></i>' };
      case "lifestyle":
        return { boxClass: "icon-lifestyle",    iconHtml: '<i class="fa-solid fa-icons"></i>' };
      case "therapeutic":
        return { boxClass: "icon-medicament",   iconHtml: '<i class="fa-solid fa-prescription-bottle-medical"></i>' };
      case "exam":
        return { boxClass: "icon-default",      iconHtml: '<i class="fa-solid fa-clipboard-check"></i>' };
      default:
        return { boxClass: "icon-default",      iconHtml: '<i class="fa-solid fa-circle-check"></i>' };
    }
  };

  // Tri + regroupement (même logique)
  recosForObjective.sort((a,b)=> (a?.label ?? "").localeCompare(b?.label ?? "", "fr", {sensitivity:"base"}));
  const groups = new Map();
  for (const r of recosForObjective) {
    const key = normalizeType(r?.type);
    if (!groups.has(key)) groups.set(key, []);
    groups.get(key).push(r);
  }
  const orderedKeys = [
    ...typeOrder.filter(k => groups.has(k)),
    ...Array.from(groups.keys()).filter(k => !typeOrder.includes(k)).sort()
  ];

  // HTML final (identique au rendu de la fonction par slide)
  let html = ""; //`<span style="font-size:24px; color:#b88c63; font-biomarker_weight:700;">Recommandations</span><br><br>`;
  for (const key of orderedKeys) {
