function isNormalSlide(slideCode, cycle = "n") {


  let mCuType = cuType();

  if (isCU3()) {
    mCuType = "CU2";
  }


  const group = "deck_" + mCuType;
  const markers = findMarkersBySlide(slideCode, group);

  if (!Array.isArray(markers) || markers.length === 0) {
    return "";
  }

  // Récupère d'abord le code, sinon le libellé, puis normalise
  const vals = markers
    .map(m => {
      let v = getReNG(m, cycle, "interpretationCode");
      if (!v) v = getReNG(m, cycle, "interpretation");
     // console.warn(`[isNormalSlide2] ${m} ->`, v);
      return (v ?? "").toString().trim().toLowerCase();
    })
    .filter(Boolean);

  if (vals.length === 0) {
    return "";
  }

  // Tout est normal/optimal ?
  const hasAbnormal = vals.some(v => v !== "normal" && v !== "optimal");
  return !hasAbnormal;
}

