function findSlideTitlesByObjectiveCode(
  cycle = "n",
  objectiveIndex = 0,
  group = null,           // "deck_CU1", "reco_CU1", etc. ou null = tous
  lang = "FR",            // "FR" ou "EN"
  join = true,
  separator = "\n",
  unique = true
) {
  // 1) Récupère la liste des codes slides via la fonction précédente
  const slideCodes = findSlidesByObjective(cycle, objectiveIndex, group, false, separator, unique);
  if (!Array.isArray(slideCodes)) {
    return slideCodes; // peut être une string d'erreur
  }

  // 2) Contexte titres & objectifs
  const mtitresMap = window.titresMap;
  if (!mtitresMap || typeof mtitresMap.get !== "function") {
    return "❌ titresMap introuvable ou invalide";
  }

  const objectives = window.checkupMap?.get?.("checkups")?.[cycle]?.objectives;
  if (!Array.isArray(objectives)) {
    return `❌ Pas d'objectives trouvés pour le cycle "${cycle}"`;
  }
  if (objectiveIndex < 0 || objectiveIndex >= objectives.length) {
    return `❌ L'objectif ${objectiveIndex} n'existe pas (max = ${objectives.length - 1})`;
  }
  const wantedSet = new Set(objectives[objectiveIndex]?.observationCodes || []);

  // 3) Construit "- <b>Titre</b> [#code] (bio1 bio2)"
  const rows = [];

  for (const slideCode of slideCodes) {
    const obj = mtitresMap.get(slideCode);
    if (!obj) {
      console.warn(`⚠️ Aucun titre trouvé pour le code slide "${slideCode}"`);
      continue;
    }
    const key = lang === "FR" ? "Titre Slide FR" : "Titre Slide EN";
    const titre = obj[key] || slideCode;

    // Récupère les bio-codes associés à ce slide (et filtre par l’objectif courant)
    let bios = [];
    try {
      const found = findMarkersBySlide(slideCode, group, false) || [];
      bios = found.filter(code => wantedSet.has(code));
    } catch (e) {
      console.warn(`⚠️ findMarkersBySlide a échoué pour "${slideCode}" :`, e);
      bios = [];
    }

    // Dédupe et ordonne les bio-codes
    const biosFinal = Array.from(new Set(bios)).sort((a, b) =>
      a.localeCompare(b, "en", { sensitivity: "base" })
    );

    // Construction de la ligne avec titre + code slide + bio-codes
    let line = `- <b>${titre}</b> [${slideCode}]`;
    if (biosFinal.length > 0) {
      line += ` (${biosFinal.join(" ")})`;
    }
    line += "<br>";

    rows.push(line);
  }

  // 4) Dédupe (sur la ligne complète) si demandé
  const finalList = unique ? Array.from(new Set(rows)) : rows;

  // 5) Tri alphabétique sur l’étiquette affichée (titre + codes)
  finalList.sort((a, b) =>
    a.localeCompare(b, lang === "FR" ? "fr" : "en", { sensitivity: "base" })
  );

  return join ? finalList.join(separator) : finalList;
}

function findSlideTitlesByObjective(
  cycle = "n",
  objectiveIndex = 0,
  group = null,           // "deck_CU1", "reco_CU1", etc. ou null = tous
  lang = "FR",            // "FR" ou "EN"
  join = true,
  separator = ",\n",
  unique = true
) {
  // Récupère la liste des codes slides via la fonction précédente
