      console.warn(`⚠️ Aucun titre trouvé pour le code ${code}`);
      continue;
    }
    const key = lang === "FR" ? "Titre Slide FR" : "Titre Slide EN";
    titres.push(obj[key] || code);
  }

  let finalList = unique ? Array.from(new Set(titres)) : titres;

  // Tri alphabétique simple
  finalList.sort((a, b) => a.localeCompare(b, lang === "FR" ? "fr" : "en", { sensitivity: "base" }));

  return join ? finalList.join(separator) : finalList;
}

