function findSlidesByObjective(
  cycle = "n",
  objectiveIndex = 0,
  group = null,        // ex: "deck_CU1", "reco_CU1
  join = true, separator = ", ", unique = true) {

  // Récupère la liste des observations de l'objectif
  const observations = listObservationsByObjective(cycle, objectiveIndex, false);
  if (!Array.isArray(observations)) {
    return observations; // retourne le message d'erreur de listObservationsByObjective
  }

  let allSlides = [];

  // Pour chaque observation -> ajoute ses slides
  for (const obs of observations) {
    const slides = findSlidesByObservation(obs, group, false, separator, unique);
    if (Array.isArray(slides)) {
      allSlides.push(...slides);
    }
  }

  if (unique) {
    allSlides = Array.from(new Set(allSlides));
  }

  return join ? allSlides.join(separator) : allSlides;
}

function findSlidesByObservation(
  observationKey,
  group = null,            // ex: "deck_CU1" | "reco_CU1" | null (tous)
  join = true,
  separator = ", ",
  unique = true
) {
  // Normalise Map -> objet simple
  const map = window.markerSlideMapNG instanceof Map
    ? Object.fromEntries(window.markerSlideMapNG)
    : window.markerSlideMapNG;

  if (!map || typeof map !== "object") {
    return "❌ markerSlideMapNG introuvable ou invalide";
  }

  const entry = map[observationKey];
  if (!entry || typeof entry !== "object") {
    return `❌ Observation inconnue: ${observationKey}`;
  }

  // Sélectionne les clés de type "..._slide1", "..._slide2", etc.
  let pairs = Object.entries(entry).filter(([k]) => /_slide\d+$/.test(k));

  // Si un groupe est demandé (ex: "deck_CU1"), on le filtre
  if (group) {
    const prefix = group + "_slide"; // "deck_CU1_slide"
    pairs = pairs.filter(([k]) => k.startsWith(prefix));
  }

  // Récupère les valeurs non null/undefined/""
  let slides = pairs.map(([, v]) => v).filter(Boolean);

  if (unique) slides = Array.from(new Set(slides));

  return join ? slides.join(separator) : slides;
}

