function getNextSlideCode(actualSlideCode) {

  let map = window.flow;

  if (!(map instanceof Map)) {
    console.error("❌ Le deuxième argument doit être une Map");
    return null;
  }

  const entries = Array.from(map.values());
  const index = entries.findIndex(e => e.slide === actualSlideCode);

  if (index === -1) {
    console.warn("⚠️ Slide non trouvé :", actualSlideCode);
    return null;
  }

  // Parcours des slides suivants
  for (let i = index + 1; i < entries.length; i++) {
    const { slide, condition } = entries[i];

    let ok = true;
    if (condition) {
      try {
        ok = eval(condition); // ⚠️ à sécuriser si input non contrôlé
      } catch (e) {
        console.error("Erreur dans la condition du slide", slide, ":", e);
        ok = false;
      }
    }

    if (ok) {
      return slide;
    }
  }

  // rien trouvé
  return null;
}

