function listRecommendationsForObjectiveByTypeStyled(
  cycle = "n",
  objectiveIndex = 0,
  {
    typeOrder = ["exam", "therapeutic", "supplement", "nutrition", "lifestyle", "coaching", "other"],
  } = {}
) {
  // --- Vérifs de base ---
  if (!window.checkupMap || typeof checkupMap.get !== "function") {
    return "❌ checkupMap introuvable ou invalide";
  }
  const scope = checkupMap.get("checkups")?.[cycle];
  if (!scope) return `❌ Pas de données pour le cycle "${cycle}"`;

  const objectives = scope.objectives;
  const recosRaw   = scope.recommendations;

  if (!Array.isArray(objectives)) return `❌ Pas d'objectifs trouvés pour le cycle "${cycle}"`;
  if (objectiveIndex < 0 || objectiveIndex >= objectives.length) {
    return `❌ L'objectif ${objectiveIndex} n'existe pas (max = ${objectives.length - 1})`;
  }
  if (!recosRaw) return `❌ Pas de recommandations trouvées pour le cycle "${cycle}"`;

  const objective = objectives[objectiveIndex];

  // --- Codes de l’objectif (ton schéma) ---
  const objectiveCodes = new Set([
    ...(objective?.observationCodes || []),
  ].filter(Boolean));

  if (objectiveCodes.size === 0) {
    console.warn("⚠️ Objectif sans observationCodes :", { objectiveIndex, objective });
  }

  // --- Normaliser recos en tableau ---
  const allRecos = recosRaw instanceof Map
    ? Array.from(recosRaw.values())
    : (Array.isArray(recosRaw) ? recosRaw : Object.values(recosRaw));

  // --- Cibles d'une reco (ton schéma = observationCodes) + tolérance ---
  const getRecoTargets = (r) => {
    const items = (r?.items || [])
      .map(it => (typeof it === "string" ? it : (it?.code ?? it?.id)))
      .filter(Boolean);

    return new Set([
      ...(r?.observationCodes || []), // PRINCIPAL dans ta capture
      ...(r?.targets || []),
      ...(r?.targetCodes || []),
      ...items,
    ].filter(Boolean));
  };

  // --- Filtrer: intersection observationCodes ---
  const recosForObjective = [];
  let emptyTargets = 0, noMatch = 0;

  for (const r of allRecos) {
    const targets = getRecoTargets(r);
    if (targets.size === 0) { emptyTargets++; continue; }
    const match = [...targets].some(code => objectiveCodes.has(code));
    if (match) recosForObjective.push(r); else noMatch++;
  }

  if (recosForObjective.length === 0) {
    console.warn("🕵️ Aucune reco liée à l’objectif", {
      objectiveIndex, objectiveCodes: [...objectiveCodes],
      totalRecos: allRecos.length, emptyTargets, noMatch
    });
    return "Aucune recommandation trouvée pour cet objectif.";
  }

  // --- Groupement par type ---
  const grouped = {};
  for (const r of recosForObjective) {
    const key = (typeof r?.type === "string" ? r.type.toLowerCase() : (r?.category || "other")).toLowerCase?.() || "other";
    (grouped[key] ||= []).push(r);
  }

  // --- Rendu HTML ---
  let html = "";
  const renderItems = (items) => {
    let h = "<ul>";
    for (const r of items) {
      const label = r?.label ?? r?.recommendationLabel ?? r?.title ?? r?.name ?? "—";
      const rationale = r?.description ?? r?.rationale ?? r?.reason ?? "";
      h += `<li>${label}${rationale ? ` <span class="rationale">(${escapeHtml(rationale)})</span>` : ""}</li>`;
    }
    h += "</ul>";
    return h;
  };

  for (const t of typeOrder) {
    const items = grouped[t];
    if (!items?.length) continue;
    html += `<div class="reco-section"><h4>${t.toUpperCase()}</h4>${renderItems(items)}</div>`;
  }
  for (const t of Object.keys(grouped)) {
    if (typeOrder.includes(t)) continue;
    html += `<div class="reco-section"><h4>${t.toUpperCase()}</h4>${renderItems(grouped[t])}</div>`;
  }

  return html || "Aucune recommandation à afficher.";

  function escapeHtml(str) {
    if (typeof str !== "string") return str;
    return str
      .replaceAll("&", "&amp;")
      .replaceAll("<", "&lt;")
      .replaceAll(">", "&gt;")
      .replaceAll('"', "&quot;")
      .replaceAll("'", "&#39;");
  }
}

function findMarkersBySlideCUI(slideCode, join = false, separator = ", " ) {
  const map = markerSlideMap instanceof Map ? Object.fromEntries(markerSlideMap) : markerSlideMap;
  const result = [];

  for (const [key, entry] of Object.entries(map)) {
    if (Array.isArray(entry)) {
      const list = entry.map(o => o?.["Slide CUI"]).filter(Boolean);
      if (list.includes(slideCode)) result.push(key);
    } else {
      if (entry?.["Slide CUI"] === slideCode) result.push(key);
    }
  }

 return join ? result.join(separator) : result;
}

function findMarkersBySlide(slideCode, group = null, join = false, separator = ", ") {
  // Normalise Map -> plain object
  const map = markerSlideMapNG instanceof Map
    ? Object.fromEntries(markerSlideMapNG)
    : markerSlideMapNG;

  const result = [];

  for (const [key, entry] of Object.entries(map || {})) {
    if (entry && typeof entry === "object") {
      let valuesToCheck;

      if (group) {
        // Ne garde que les clés du groupe demandé (ex: "deck_CU1_slide1/2")
        valuesToCheck = Object.entries(entry)
          .filter(([k]) => k.startsWith(group + "_slide"))
          .map(([, v]) => v)
          .filter(Boolean);
      } else {
        // Sinon, regarde toutes les valeurs définies (uniquement slide1/2 etc.)
        valuesToCheck = Object.entries(entry)
          .filter(([k]) => /_slide\d+$/.test(k))
          .map(([, v]) => v)
          .filter(Boolean);
      }

      if (valuesToCheck.includes(slideCode)) {
        result.push(key);
      }
    }
  }

  return join ? result.join(separator) : result;
}

function listRecommendationsForObjective(cycle = "n", objectiveIndex = 0, asHtml = true) {
  // Vérifs de base
  if (!window.checkupMap || typeof checkupMap.get !== "function") {
    return "❌ checkupMap introuvable ou invalide";
  }
  const checkups = checkupMap.get("checkups");
  const objectives = checkups?.[cycle]?.objectives;
  const recosMap   = checkups?.[cycle]?.recommendations;

  if (!objectives) return `❌ Pas d'objectifs trouvés pour le cycle "${cycle}"`;
  if (!recosMap)   return `❌ Pas de recommandations trouvées pour le cycle "${cycle}"`;
  if (objectiveIndex < 0 || objectiveIndex >= objectives.length) {
    return `❌ L'objectif ${objectiveIndex} n'existe pas (max = ${objectives.length - 1})`;
  }

  const obj = objectives[objectiveIndex];
  const objId = obj?.id ?? obj?.objectiveId ?? null;
  const observationCodes = Array.isArray(obj?.observationCodes) ? obj.observationCodes : [];

  // Normalise recos -> tableau
  const allRecos = Object.values(recosMap);

  // 1) par objectiveIds
  let recos = allRecos.filter(r =>
    Array.isArray(r?.objectiveIds) && objId && r.objectiveIds.includes(objId)
  );

  // 2) secours par itemKey
  if (recos.length === 0 && observationCodes.length > 0) {
    recos = allRecos.filter(r => r?.itemKey && observationCodes.includes(r.itemKey));
  }


  if (recos.length === 0) {
    return `<b>Recommandations pour l'objectif ${objectiveIndex} (${obj?.label ?? "sans label"})</b><br>(aucune recommandation liée trouvée)`;
  }

  // Tri par label (FR si fourni, sinon brut)
  recos.sort((a, b) => (a?.label ?? "").localeCompare(b?.label ?? "", "fr", {sensitivity:"base"}));

  let out = `Recommandations pour l'objectif ${objectiveIndex} (${obj?.label ?? "sans label"})${br}`;
  recos.forEach((r, i) => {
    const type = r?.type ?? "❓type inconnu";
    const label = r?.label ?? "❓label manquant";
    const description = r?.description ?? "";

    out += `<ul><li>${label} (${type})<br> <span style="font-size:14px; color:#aaaaaa;">${description}</span></li></ul><br>`;
  });

  return out.trim();
}

function getSlideCUI(key, { all = false, index = 0 } = {}) {
const map = markerSlideMap;
  const entry = map instanceof Map ? map.get(key) : map?.[key];
  if (!entry) return all ? [] : undefined;

  if (Array.isArray(entry)) {
    const list = entry.map(o => o?.["Slide CUI"]).filter(Boolean);
    return all ? list : list[index];
  }
  return entry?.["Slide CUI"];
}

function getMakerSlides(key, entryKey) {

  const map = markerSlideMapNG;

  const entry = map instanceof Map ? map.get(key) : map?.[key];
  if (!entry) return undefined;

  if (!entryKey) {
    // si pas de clé interne précisée, je retourne toute la Map
    return new Map(Object.entries(entry));
  }

  // sinon je retourne directement la valeur demandée
  return entry[entryKey];
}

