window.collectSlidesByObjectiveNG = function(
  cycle = "n",
  objectiveIndex = 0,
  { group = null } = {},
  titreSlides = [],
  codeSlides = [],
) {


  const lang = window.lang;

  const objectives = checkupMap.get("checkups")?.[cycle]?.objectives;
  if (!objectives) return `❌ Pas d'objectives trouvés pour le cycle "${cycle}"`;
  if (objectiveIndex < 0 || objectiveIndex >= objectives.length) {
    return `❌ L'objectif ${objectiveIndex} n'existe pas (max = ${objectives.length - 1})`;
  }

  const obj = objectives[objectiveIndex];
  const wantedItems = new Set(obj?.observationCodes || []);

  // normalise markerSlideMapNG -> objet simple
  const map = markerSlideMapNG instanceof Map
    ? Object.fromEntries(markerSlideMapNG)
    : (markerSlideMapNG || {});

  // 1) Lister tous les slideCodes possibles selon le group
  const allSlideCodes = new Set();
  for (const entry of Object.values(map)) {
    if (!entry || typeof entry !== "object") continue;

    const pairs = Object.entries(entry);
    if (group) {
      // ex: "deck_CU1_slide1/2"
      for (const [k, v] of pairs) {
        if (k.startsWith(group + "_slide") && v) allSlideCodes.add(v);
      }
    } else {
      for (const [k, v] of pairs) {
        if (/_slide\d+$/.test(k) && v) allSlideCodes.add(v);
      }
    }
  }

  // 2) Garder seulement les slides qui contiennent au moins un item de l’objectif
  const keptSlides = [];
  for (const slideCode of allSlideCodes) {
    const markers = findMarkersBySlide(slideCode, group); // markers liés à ce slide
    if (!markers || markers.length === 0) continue;

    const hasOne = markers.some(m => wantedItems.has(m));
    if (hasOne) keptSlides.push(slideCode);
  }

  // 3) Trier par titre FR (fallback = code)
  keptSlides.sort((a, b) => {
    const titreA = getTitre(a, lang) || a;
    const titreB = getTitre(b, lang) || b;
    return titreA.localeCompare(titreB, lang, { sensitivity: "base" });
  });

  // 4) Vider puis remplir les tableaux en sortie
  titreSlides.length = 0;
  codeSlides.length = 0;

  let result = `--- Objective ${objectiveIndex + 1} (groupé par slide avec titre FR${group ? ` • ${group}` : ""}) ---\n`;
  for (const slide of keptSlides) {
    const titreFR = getTitre(slide, lang) || slide;
    titreSlides.push(titreFR);
    codeSlides.push(slide);
    result += `${titreFR} (${slide})\n`;
  }

  return result.trim();
};

