function groupItemsBySlideWithTitre(cycle = "n", objectiveIndex = 0) {
  const objectives = checkupMap.get("checkups")?.[cycle]?.objectives;
  if (!objectives) {
    return `❌ Pas d'objectives trouvés pour le cycle "${cycle}"`;
  }
  if (objectiveIndex < 0 || objectiveIndex >= objectives.length) {
    return `❌ L'objectif ${objectiveIndex} n'existe pas (max = ${objectives.length - 1})`;
  }

  const obj = objectives[objectiveIndex];
  const groups = new Map(); // slide -> [items]

  for (const item of obj.observationCodes) {
    const slide = getSlideCUI(item);
    if (!slide) continue;
    if (!groups.has(slide)) groups.set(slide, []);
    groups.get(slide).push(item);
  }

  // transformer en tableau pour trier par titre FR
  const sorted = [...groups.entries()].sort(([slideA], [slideB]) => {
    const titreA = getTitre(slideA, "FR") || "";
    const titreB = getTitre(slideB, "FR") || "";
    return titreA.localeCompare(titreB, "fr", { sensitivity: "base" });
  });

  // rendu avec titre FR
  let result = `--- Objective ${objectiveIndex + 1} (groupé par slide avec titre FR) ---\n`;
  for (const [slide, items] of sorted) {
    const titreFR = getTitre(slide, "FR");
    result += `${titreFR} (${slide})\n`;
  }

  return result.trim();
}

function getRecommendation(cycle = "n", objectiveIndex = 0, recNum = 0) {
  const checkups = checkupMap.get("checkups");
  if (!checkups) {
    return `❌ Pas de checkups trouvés`;
  }

  const objectives = checkups?.[cycle]?.objectives;
  if (!objectives || objectives.length === 0) {
    return `❌ Pas d'objectives trouvés pour le cycle "${cycle}"`;
  }

  const obj = objectives[objectiveIndex];
  if (!obj) {
    return `❌ Objectif ${objectiveIndex} introuvable`;
  }

  const recId = obj.recommendationIds?.[recNum];
  if (!recId) {
    return `❌ Aucune recommandation trouvée dans l'objectif ${objectiveIndex}`;
  }

  const recommendation = checkups?.[cycle]?.recommendations?.[recId];
  if (!recommendation) {
    return `❌ Impossible de retrouver la recommandation ${recId}`;
  }

  // rendu lisible
  return `✅ Recommandation #${recNum} de l'objectif ${objectiveIndex + 1} :
- ID : ${recommendation.id}
- Type : ${recommendation.type}
- Label : ${recommendation.label}
- Description : ${recommendation.description || "(pas de description)"}
- Domaine : ${recommendation.domain || "(inconnu)"} / ${recommendation.subDomain || "(inconnu)"}`;
}

function groupItemsBySlide(cycle = "n", objectiveIndex = 0) {
  const objectives = checkupMap.get("checkups")?.[cycle]?.objectives;
  if (!objectives) {
    return `❌ Pas d'objectives trouvés pour le cycle "${cycle}"`;
  }
  if (objectiveIndex < 0 || objectiveIndex >= objectives.length) {
    return `❌ L'objectif ${objectiveIndex} n'existe pas (max = ${objectives.length - 1})`;
  }

  const obj = objectives[objectiveIndex];
  const groups = new Map(); // slide -> [items]

  for (const item of obj.observationCodes) {
    const slide = getSlideCUI(item);
    if (!slide) continue; // pas de mapping, on saute
    if (!groups.has(slide)) groups.set(slide, []);
    groups.get(slide).push(item);
  }

  // rendu lisible
  let result = `--- Objective ${objectiveIndex + 1} (groupé par slide) ---\n`;
  for (const [slide, items] of groups) {
