function showImageWithPointOnCircle({
  divId,
  imageUrl,
  centerX,
  centerY,
  diameter,
  angleDeg,
  pointSize = 10,
  secondAngleDeg = null,   // angle pour le cercle transparent
  secondPointSize = 10
}) {
  const div = document.getElementById(divId);
  if (!div) return;

  const r = diameter / 2;

  // Point B (plein noir profond)
  let px = null, py = null;
  if (angleDeg !== undefined && angleDeg !== null && angleDeg !== "na") {
    const phiDeg = 180 - angleDeg;
    const phi = phiDeg * Math.PI / 180;
    px = centerX + r * Math.cos(phi);
    py = centerY - r * Math.sin(phi);
  }

  // Point A (cercle gris foncé transparent)
  let px2 = null, py2 = null;
  if (secondAngleDeg !== null && secondAngleDeg !== undefined && secondAngleDeg !== "na") {
    const phi2Deg = 180 - secondAngleDeg;
    const phi2 = phi2Deg * Math.PI / 180;
    px2 = centerX + r * Math.cos(phi2);
    py2 = centerY - r * Math.sin(phi2);
  }

  div.innerHTML = `
    <div style="position: relative; display: inline-block;">
      <img src="${imageUrl}" alt="Image">

      ${
        (px !== null && py !== null)
          ? `<div style="
              position:absolute;
              left:${px}px; top:${py}px;
              width:${pointSize}px; height:${pointSize}px;
              border-radius:50%;
              background:#222;               /* plein noir profond */
              transform:translate(-50%, -50%);
            "></div>`
          : ""
      }

      ${
        (px2 !== null && py2 !== null)
          ? `<div style="
              position:absolute;
              left:${px2}px; top:${py2}px;
              width:${secondPointSize}px; height:${secondPointSize}px;
              border-radius:50%;
              background:transparent;        /* intérieur transparent */
              border:3px solid #888;         /* contour gris foncé */
              transform:translate(-50%, -50%);
            "></div>`
          : ""
      }
    </div>
  `;
}

function calculateValueBioInRange(x1, x2, value) {
  const y1 = 71; // bas de la bande
  const y2 = 36; // haut de la bande
  var m = (y2 - y1) / (x2 - x1);
  var b = y1 - m * x1;
  var x = value;
  var y = m * x + b;
  return y;
}

function imageGraphGridPOC({
  divId,
  imageUrl = "https://i.imgur.com/h7crh36.png",
  width = 700,
  height = 500,
  gridX0, gridY0,
  gridX1, gridY1,
  valX, xMin, xMax,
  valY, yMin, yMax,
  valX2 = null, valY2 = null,
  secondPointSize = 20,   // diamètre du point B (inchangé)
  invertY = true,
  title = "",
  line = true,
  rangeMin = null,
  rangeMax = null,
  realValueB = null,
  forceBlack = false       // ⬅️ Nouveau paramètre
}) {
  const NS = "http://www.w3.org/2000/svg";
  const XLINK = "http://www.w3.org/1999/xlink";

  const host = document.getElementById(divId);
  if (!host) return;

  const map   = (v,a,b,A,B) => (a===b ? null : A + ((v-a)/(b-a))*(B-A));
  const clamp = (v,a,b) => Math.max(Math.min(v, Math.max(a,b)), Math.min(a,b));
  const toNumberOrNull = (v) => {
    if (v === undefined || v === null) return null;
    if (typeof v === "string") {
      const s = v.trim().toLowerCase();
      if (s === "na" || s === "") return null;
      const n = Number(v);
      return Number.isFinite(n) ? n : null;
    }
    return Number.isFinite(v) ? v : null;
  };
  const isPresentNonZero = (v) => {
    const n = toNumberOrNull(v);
    return n !== null && n !== 0;
  };

  if (host.__igrObserver) { host.__igrObserver.disconnect(); host.__igrObserver = null; }
  host.innerHTML = "";
  host.style.position = "relative";
  host.style.aspectRatio = `${width} / ${height}`;

  const svg = document.createElementNS(NS, "svg");
  svg.setAttribute("viewBox", `0 0 ${width} ${height}`);
  svg.setAttribute("preserveAspectRatio", "xMidYMid meet");
  svg.style.width = "100%";
  svg.style.height = "100%";
  host.appendChild(svg);

  const img = document.createElementNS(NS, "image");
  img.setAttributeNS(null, "href", imageUrl);
  img.setAttributeNS(XLINK, "href", imageUrl);
  img.setAttribute("x", 0); img.setAttribute("y", 0);
  img.setAttribute("width", "100%"); img.setAttribute("height", "100%");
  svg.appendChild(img);

  const xFromVal = (vx) => map(vx, xMin, xMax, gridX0, gridX1);
  const yFromVal = (vy) => invertY ? map(vy, yMin, yMax, gridY1, gridY0)
                                   : map(vy, yMin, yMax, gridY0, gridY1);

  // ----- Point A -----
  const vxA0 = toNumberOrNull(valX);
  const vyA0 = toNumberOrNull(valY);
  const aPresent = isPresentNonZero(valX) && isPresentNonZero(valY);

  let ax = null, ay = null, hasA = false;
  if (aPresent) {
    const axTmp = xFromVal(vxA0);
    const ayTmp = yFromVal(vyA0);
    if (Number.isFinite(axTmp) && Number.isFinite(ayTmp)) {
      ax = clamp(axTmp, gridX0, gridX1);
      ay = clamp(ayTmp, gridY0, gridY1);
      hasA = true;
    }
  }

  // ----- Point B -----
  const vxB0 = toNumberOrNull(valX2);
  const vyB0 = toNumberOrNull(valY2);
  const bPresent = isPresentNonZero(valX2) && isPresentNonZero(valY2);

  let bx = null, by = null, hasB = false;
  if (bPresent) {
    const bxTmp = xFromVal(vxB0);
    const byTmp = yFromVal(vyB0);
    if (Number.isFinite(bxTmp) && Number.isFinite(byTmp)) {
      bx = clamp(bxTmp, gridX0, gridX1);
      by = clamp(byTmp, gridY0, gridY1);
      hasB = true;
    }
  }

  // Ligne uniquement si les deux points existent et doivent s’afficher
  if (hasA && hasB && line) {
    const l = document.createElementNS(NS, "line");
    l.setAttribute("x1", ax); l.setAttribute("y1", ay);
    l.setAttribute("x2", bx); l.setAttribute("y2", by);
    l.setAttribute("stroke", "#DEEDE7");
    l.setAttribute("stroke-width", "2");
    l.setAttribute("vector-effect", "non-scaling-stroke");
    svg.appendChild(l);
  }

  const pointsLayer = document.createElementNS(NS, "g");
  svg.appendChild(pointsLayer);

  const realB = toNumberOrNull(realValueB);
  const inRange = (realB != null && rangeMin != null && rangeMax != null)
    ? (realB >= Math.min(rangeMin, rangeMax) && realB <= Math.max(rangeMin, rangeMax))
    : null;

  const drawPoints = () => {
    pointsLayer.innerHTML = "";
    const r = svg.getBoundingClientRect();
    const vb = svg.viewBox.baseVal;
    const scale = Math.max(((r.width / vb.width) + (r.height / vb.height)) / 2, 1e-6);

    // --- POINT A : cercle vide ---
    if (hasA) {
      const a = document.createElementNS(NS, "circle");
      a.setAttribute("cx", ax);
      a.setAttribute("cy", ay);
      a.setAttribute("r", (14 / 2) / scale);
      a.setAttribute("fill", "none");
      a.setAttribute("stroke", forceBlack ? "#888" : "#aaa"); // ⬅️ gris plus foncé si forceBlack
      a.setAttribute("stroke-width", (3 / scale));
      a.setAttribute("vector-effect", "non-scaling-stroke");
      pointsLayer.appendChild(a);
    }

    // --- POINT B : cercle plein ---
    if (hasB) {
      const b = document.createElementNS(NS, "circle");
      b.setAttribute("cx", bx);
      b.setAttribute("cy", by);
      b.setAttribute("r", (secondPointSize / 2) / scale);
      b.setAttribute("fill", forceBlack
        ? "#222" // ⬅️ noir profond si forceBlack
        : (inRange === null ? "gray" : (inRange ? "#52c376" : "#fab152"))
      );
      pointsLayer.appendChild(b);
    }
  };

  drawPoints();
  if (typeof ResizeObserver !== "undefined") {
    host.__igrObserver = new ResizeObserver(drawPoints);
    host.__igrObserver.observe(host);
  } else {
    window.addEventListener("resize", drawPoints);
  }
}

