function bioGraph({
  divId,
  goodRangeMin,
  goodRangeMax,
  realA,
  realB,
  title,
  unit,
  labelP1 = null,
  labelP2 = null
}) {
  const div = document.getElementById(divId);
  if (!div) return;

  console.warn("title:" + title + "+ realA:" + realA + "+ realB:" + realB + "+");

  // Si A et B absents → carré blanc
  if ((realA == null || Number.isNaN(realA)) && (realB == null || Number.isNaN(realB))) {
    div.innerHTML = `
      <div style="
        position: relative;
        display: inline-block;
        width:100%;
        height:100%;
        background:#FFFFFF;
      "></div>
    `;
    return;
  }

  // Normalisation des valeurs
  realA = (realA !== undefined && realA !== null) ? round2(realA) : null;
  realB = (realB !== undefined && realB !== null) ? round2(realB) : null;

  // Dimensions réelles du conteneur (H peut être 100, 120, etc.)
  const H = Math.max(1, div.clientHeight || 100);

  // --- Mise à l'échelle verticale depuis le repère "ancien 120px" ---
  const scaleY120 = (y) => (y / 120) * H;

  // Anciennes bornes (dessin d'origine basé sur 120px de haut)
  const PAD_TOP_OLD = 20;  // marge top dans l'ancien repère
  const PAD_BOT_OLD = 85;  // limite basse utile dans l'ancien repère

  // Bornes recalculées pour la hauteur courante
  const padTop = scaleY120(PAD_TOP_OLD);
  const padBottom = scaleY120(PAD_BOT_OLD);

  // Clamp vertical dans le repère actuel
  const clampY = (y) => Math.max(padTop, Math.min(padBottom, y));

  // Calcule la position Y en ré-échantillonnant la sortie historique
  // de calculateValueBioInRange (conçue pour 120px) vers H
  const scaledValueY = (min, max, val) => {
    const yOld = calculateValueBioInRange(min, max, val); // supposé rendre un Y en "ancien repère"
    const yNow = scaleY120(yOld);
    return clampY(yNow);
  };

  // Image & points
//  const imageUrl = "https://i.postimg.cc/7brvKqZ7/back-bio.png";
 let imageUrl = "https://raw.githubusercontent.com/Fabrice2072/ZFlow/refs/heads/main/bio.png";

 if (goodRangeMin == 0) imageUrl = "https://raw.githubusercontent.com/Fabrice2072/ZFlow/refs/heads/main/bio0.png";
 if (goodRangeMax == 200) imageUrl = "https://raw.githubusercontent.com/Fabrice2072/ZFlow/refs/heads/main/bio200.png";


  // Taille des points proportionnelle à H (bornée)
  const pointSize = Math.max(6, Math.min(16, Math.round((12 / 120) * H)));
  const secondPointSize = pointSize;

  // X inchangés (si tu veux, on pourra aussi les rendre proportionnels à la largeur)
  const aX = 120;
  const bX = 215;

  // Positions Y des points
  const aY = (realA != null) ? scaledValueY(goodRangeMin, goodRangeMax, realA) : null;
  const bY = (realB != null) ? scaledValueY(goodRangeMin, goodRangeMax, realB) : null;

  const inGoodRange = (v) => (v != null && v >= goodRangeMin && v <= goodRangeMax);
  const colorA = (realA != null) ? (inGoodRange(realA) ? "#4CAF50" : "#FFA726") : "#4CAF50";
  const colorB = (realB != null) ? (inGoodRange(realB) ? "#4CAF50" : "#FFA726") : "#4CAF50";

  const pointHTML = ({ x, y, size, color, kind }) => {
    if (y == null) return '';
    if (kind === 'outlined') {
      return `<div style="position:absolute; left:${x}px; top:${y}px; width:${size}px; height:${size}px; border-radius:50%; background:white; border:3px solid ${color}; transform:translate(-50%, -50%);"></div>`;
    }
    return `<div style="position:absolute; left:${x}px; top:${y}px; width:${size}px; height:${size}px; border-radius:50%; background:${color}; transform:translate(-50%, -50%);"></div>`;
  };

  const labelHTML = ({ left=5, top, text, color='#9E9E9E', bold=false }) => `
    <div style="position:absolute; left:${left}px; top:${top}px; font-size:10px; font-family:Arial, sans-serif; color:${color}; font-weight:${bold ? 'bold' : 'normal'}; transform:translateY(-50%);">${text}</div>`;

  const hasA = (realA != null && aY != null && !Number.isNaN(aY));
  const hasB = (realB != null && bY != null && !Number.isNaN(bY));

  const labelTextP1 = hasA ? (labelP1 ?? `${realA}${unit ? ' ' + unit : ''}`) : null;
  const labelTextP2 = hasB ? (labelP2 ?? `${realB}${unit ? ' ' + unit : ''}`) : null;

  // Y des labels min/max dans le repère actuel
  const yGoodMax = scaleY120(calculateValueBioInRange(goodRangeMin, goodRangeMax, goodRangeMax));
  const yGoodMin = scaleY120(calculateValueBioInRange(goodRangeMin, goodRangeMax, goodRangeMin));

  // 🎯 Couleur conditionnelle pour le max : rouge si 200
  const goodMaxColor = (goodRangeMax === 200) ? "#FFFFFF" : "#9E9E9E";
  const goodMinColor = (goodRangeMin === 0) ? "#FFFFFF" : "#9E9E9E";

  div.innerHTML = `
    <div style="position: relative; display: inline-block; width:100%; height:100%;">
      <img src="${imageUrl}" alt="Image" style="width:100%; height:100%; object-fit:contain;">

      <!-- Labels à droite de la zone grise (position verticale re-scalée) -->
      <div style="
        position:absolute;
        left:100%;
        margin-left:-15px;
        top:${yGoodMax}px;
        transform:translateY(-50%);
        font-size:10px;
        font-family:Arial, sans-serif;
        color:${goodMaxColor};
        text-align:left;
      ">${goodRangeMax}</div>

      <div style="
        position:absolute;
        left:100%;
        margin-left:-15px;
        top:${yGoodMin}px;
        transform:translateY(-50%);
        font-size:10px;
        font-family:Arial, sans-serif;
        color:${goodMinColor};
        text-align:left;
      ">${goodRangeMin}</div>

      ${hasA && hasB ? `<svg style="position:absolute; left:0; top:0; width:100%; height:100%; pointer-events:none;"><line x1="${aX}" y1="${aY}" x2="${bX}" y2="${bY}" stroke="#DDD" stroke-width="2" /></svg>` : ""}

      <div style="position:absolute; left:5px; top:${Math.max(10, Math.round(10/120*H))}px; font-size:12px; font-family:Arial, sans-serif; font-weight:bold; color:black; transform:translateY(-50%);">${title}</div>
      ${labelTextP1 ? labelHTML({ top:Math.round(45/120*H), text: labelTextP1, color:'#9E9E9E', bold:false }) : ''}
      ${labelTextP2 ? labelHTML({ top:Math.round(65/120*H), text: labelTextP2, color: colorB, bold:true }) : ''}

      ${hasA ? pointHTML({ x:aX, y:aY, size:pointSize, color:colorA, kind:'outlined' }) : ''}
      ${hasB ? pointHTML({ x:bX, y:bY, size:secondPointSize, color:colorB, kind:'filled' }) : ''}
    </div>
  `;
}

