/**
 * Fonction getCU améliorée pour naviguer dans les données JSON
 * Supporte les chemins profonds, les tableaux et les cycles n/n-1
 * 
 * @param {string} rootKey - Clé racine dans checkupMap (ex: "checkups", "personalInfos")
 * @param {...string} path - Chemin vers la propriété désirée
 * @returns {any} La valeur trouvée ou undefined
 */
function getCUNG(rootKey, ...path) {
  console.log('🎯 getCU called with:', { rootKey, path });

  // Vérifier que checkupMap existe
  if (!window.checkupMap) {
    console.warn('⚠️ getCU: checkupMap is not defined');
    return undefined;
  }

  // Récupérer l'objet racine
  const rootData = window.checkupMap.get(rootKey);
  if (!rootData) {
    console.warn(`⚠️ getCU: No data found for root key "${rootKey}"`);
    return undefined;
  }

  // Naviguer dans le chemin
  let current = rootData;

  for (let i = 0; i < path.length; i++) {
    const key = path[i];

    if (current === null || current === undefined) {
      console.warn(`⚠️ getCU: Path interrupted at step ${i}, key "${key}"`);
      return undefined;
    }

    // Gérer les tableaux (clés numériques)
    if (Array.isArray(current)) {
      const index = parseInt(key);
      if (isNaN(index) || index < 0 || index >= current.length) {
        console.warn(`⚠️ getCU: Invalid array index "${key}" for array of length ${current.length}`);
        return undefined;
      }
      current = current[index];
    }
    // Gérer les objets
    else if (typeof current === 'object') {
      if (!(key in current)) {
        console.warn(`⚠️ getCU: Key "${key}" not found in object at step ${i}`);
        return undefined;
      }
      current = current[key];
    }
    // Type non navigable
    else {
      console.warn(`⚠️ getCU: Cannot navigate into ${typeof current} at step ${i}, key "${key}"`);
      return undefined;
    }
  }

  console.log('✅ getCU result:', current);
  return current;
}

// Rendre la fonction disponible globalement
window.getCU = getCU;

